package com.pa.ipv01.object;

public class mydate {
	int d;
	int m;
	int y;

	public mydate(int d, int m, int y) {
		super();
		// TODO Auto-generated constructor stub
		this.d = d;
		this.m = m;
		this.y = y;

	}

	public int getD() {
		return d;
	}

	public void setD(int d) {
		this.d = d;
	}

	public int getM() {
		return m;
	}

	public void setM(int m) {
		this.m = m;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}

}
